(function(){const a=document.getElementById('sidebar');const b=['H1','H2','H3','H4','H5','H6'];[...document.getElementById('main').querySelectorAll(b.join())].forEach(c=>{const d=document.createElement('a');d.href='#'+ c.id;d.textContent=c.innerText;d.style.marginLeft=0.5*b.indexOf(c.tagName)+ 'em';a.append(d)});document.getElementById('sidebar-button').addEventListener('click',()=>{a.classList.toggle('sidebar-display')});document.addEventListener('click',c=>{if(c.target.closest('#main')){a.classList.remove('sidebar-display')}})})()