(function(){const a=document.getElementById('sidebar');const b=['H1','H2','H3','H4','H5','H6'];[...document.getElementById('main').querySelectorAll(b.join())].forEach(d=>{const e=document.createElement('a');e.href='#'+ d.id;e.textContent=d.innerText;e.style.marginLeft=0.5*b.indexOf(d.tagName)+ 'em';a.append(e)});const c=document.getElementById('sidebar-button');c.addEventListener('click',()=>{a.classList.toggle('sidebar-display')})})()